package com.example.fragment;import java.util.ArrayList;import java.util.List;import android.os.Bundle;import android.support.annotation.Nullable;import android.support.v4.app.Fragment;import android.support.v4.app.FragmentManager;import android.support.v4.app.FragmentStatePagerAdapter;import android.support.v4.view.ViewPager;import android.support.v4.view.ViewPager.OnPageChangeListener;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.RadioButton;import android.widget.RadioGroup;import android.widget.RadioGroup.OnCheckedChangeListener;import com.example.q3.R;import com.nostra13.universalimageloader.core.ImageLoader;public class Fragment2 extends Fragment{	protected ImageLoader imageLoader = ImageLoader.getInstance();	private View parentView;	private ViewPager vp;	private ArrayList<Fragment> list;	private RadioGroup radioGroup;	private RadioButton mRb1;	private RadioButton mRb2;    	@Override	@Nullable	public View onCreateView(LayoutInflater inflater,			@Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {		parentView = inflater.inflate(R.layout.fragment2, container, false);		vp=(ViewPager)parentView.findViewById(R.id.mVP1);				list = new ArrayList<Fragment>();		Fragment2a tzf=new Fragment2a();		Fragment2b dtf=new Fragment2b();		list.add(tzf);		list.add(dtf);		radioGroup =(RadioGroup)parentView.findViewById(R.id.radioGroup);		mRb1 =(RadioButton)parentView.findViewById(R.id.mRb1);		mRb2 =(RadioButton)parentView.findViewById(R.id.mRb2);				ZxzcAdapter zxzc = new ZxzcAdapter(getChildFragmentManager(), list);		vp.setAdapter(zxzc);		zxzc.notifyDataSetChanged();				radioGroup.setOnCheckedChangeListener(new OnCheckedChangeListener() {			@Override			public void onCheckedChanged(RadioGroup group, int cheakedId) {				if (cheakedId == mRb1.getId()) {					vp.setCurrentItem(0);				} else if (cheakedId == mRb2.getId()) {					vp.setCurrentItem(1);				}			}		});		mRb1.setChecked(true);		vp.setOnPageChangeListener(new OnPageChangeListener() {					@Override			public void onPageSelected(int arg0) {				switch (arg0) {				case 0:						mRb1.setChecked(true);									break;				case 1:					mRb2.setChecked(true);					break;				}			}			@Override			public void onPageScrolled(int arg0, float arg1, int arg2) {			}			@Override			public void onPageScrollStateChanged(int arg0) {			}		});		return parentView;	}	class ZxzcAdapter extends FragmentStatePagerAdapter {		   				List<Fragment> list;					public ZxzcAdapter(FragmentManager fm,List<Fragment> list) {			super(fm);			this.list=list;					}		@Override		public Fragment getItem(int arg0) {						return list.get(arg0);		}		@Override		public int getCount() {					return list.size();		}	}						}